    private void BtnCalcularActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_BtnCalcularActionPerformed
        
        int MI = Integer.parseInt(JTFMeses.getText());
        double VI = Double.parseDouble(JTFVI.getText()), PC = Double.parseDouble(JTFPC.getText()), ST = 0;
        
        do {
            
            if(ST == 0){
                
                ST = VI + (VI * PC/100);
                
            } else {
                
                ST = ST + (ST * PC/100);
                
            }
            
            MI--;
            
        }while (MI > 0);
        
        JTFResultado.setText(String.valueOf(String.format("%,.2f", ST)));
        
    }//GEN-LAST:event_BtnCalcularActionPerformed

